mapper.delete:
/* 관리자페이지 단에서 삭제를 위한 쿼리 1,2,3 */
DELETE
FROM
	tb_common_user
WHERE
	user_id = #{memberId};

mapper.update:
UPDATE tb_common_user
SET 
	 user_pw		= #{memberPw}
	,user_name		= #{memberName}
	,user_email		= #{memberEmail}
	,user_addr		= #{memberAddr}
WHERE
	user_id = #{memberId}

UPDATE tb_common_user
SET 
	 user_pw		= #{memberPw}
	,user_name		= #{memberName}
	,user_email		= #{memberEmail}
	,user_addr		= #{memberAddr}
WHERE
	user_id = #{memberId}
