mapper.select:
	/* 프로모션 등록 시도할 경우, 최초 등록데이터 유무 판단(권한 체크) */
  SELECT COUNT(*) 
  FROM (
	  SELECT 
		  user_id
	FROM 
	  tb_trainer 
	WHERE 
	user_id = #{proId}
  )c;

  /* 현재 프로모션 등록 수 페이징 처리 */
SELECT
  count(*)
FROM
	tb_notice_promotion_trainer;

/*	페이징 처리  */
SELECT
*
FROM
  tb_notice_promotion_trainer
LIMIT #{startRow}, #{rowPerPage};

/*로그인된 트레이너가 자신의 올린 모든 프로모션 리스트*/ SELECT pt.trainer_promotion_notice_code, pt.trainer_promotion_name, pt.trainer_promotion_recruit_start_date, pt.trainer_promtion_recruit_end_date, pt.trainer_promotion_recruit_people
FROM tb_notice_promotion_trainer AS pt
WHERE pt.user_id = #{trainerId};
